    /**
     * @throws \Exception
     */
    public function testMultipleFiltersSearch()
    {
        $indexes = ['sm-products', 'sm-documents', 'sm-pages'];
        $index = new ElasticSearchIndexFilter(new IndexFilterParams([Product::class, Document::class, Page::class]));
        $this->assertAttributeEquals('index', 'name', $index);
        $this->assertAttributeEquals($indexes, 'values', $index);
        $this->assertAttributeEquals(null, 'langCodes', $index);

        $brandTerm = new TermsFilter(new TermsFilterParams('brandId', [333, 50]));
        $this->assertAttributeEquals('terms', 'name', $brandTerm);
        $this->assertAttributeEquals(['brandId' => [333, 50]], 'values', $brandTerm);
        $this->assertAttributeEquals(null, 'langCodes', $brandTerm);

        $attrTerm = new TermsFilter(new AttributeFilterParams(7, [279, 554]));
        $this->assertAttributeEquals('terms', 'name', $attrTerm);
        $this->assertAttributeEquals(['7_attr.valueId' => [279, 554]], 'values', $attrTerm);
        $this->assertAttributeEquals(null, 'langCodes', $attrTerm);

        $priceTerm = new TermsFilter(new TermsFilterParams('price', [6600]));
        $this->assertAttributeEquals('terms', 'name', $priceTerm);
        $this->assertAttributeEquals(['price' => [6600]], 'values', $priceTerm);
        $this->assertAttributeEquals(null, 'langCodes', $priceTerm);

        $priceRange = new PriceRangeFilter(new PriceRangeFilterParams(100, 6060));
        $this->assertAttributeEquals('range', 'name', $priceRange);
        $this->assertAttributeEquals([100, 6060], 'values', $priceRange);
        $this->assertAttributeEquals(null, 'langCodes', $priceRange);

        $match = new MatchFilter(new MatchFilterParams('title', 'test'), [$this->langCode]);
        $this->assertAttributeEquals('match', 'name', $match);
        $this->assertAttributeEquals(['title' => 'test'], 'values', $match);
        $this->assertAttributeEquals(['fr'], 'langCodes', $match);

        $matchPhrase = new MatchPhraseFilter(new MatchFilterParams('title', 'test'), [$this->langCode]);
        $this->assertAttributeEquals('match_phrase', 'name', $matchPhrase);
        $this->assertAttributeEquals(['title' => 'test'], 'values', $matchPhrase);
        $this->assertAttributeEquals(['fr'], 'langCodes', $matchPhrase);

        $dealersGeo = new DealersGeoFilter(new DealersGeoFilterParams(200, 40, -70, GeoDistanceUnits::KILOMETER));
        $this->assertAttributeEquals('geo_distance', 'name', $dealersGeo);
        $this->assertAttributeEquals([
            'distance' => '200' . GeoDistanceUnits::KILOMETER,
            'lat'      => 40,
            'lon'      => -70,
        ], 'values', $dealersGeo);
        $this->assertAttributeEquals(null, 'langCodes', $dealersGeo);

        $searchFilter = (new SearchFilter())->index([$index])->elements([
            $brandTerm,
            $attrTerm,
            $priceTerm,
            $priceRange,
            $match,
            $matchPhrase,
            $dealersGeo,
        ]);

        $elements = $searchFilter->getElements();

        $elementsArray = [
            ['terms' => ['brandId' => [333, 50]]],
            ['terms' => ['7_attr.valueId' => [279, 554]]],
            ['terms' => ['price' => [6600]]],
            ['range' => ['price' => ['gte' => 100, 'lte' => 6060]]],
            ['match' => ['title_fr' => 'test']],
            ['match_phrase' => ['title_fr' => 'test']],
            [
                'geo_distance' => [
                    'distance'           => '200km',
                    'addresses.location' => [
                        'lat' => 40,
                        'lon' => -70,
                    ],
                ],
            ],
        ];
        $this->assertAttributeEquals($indexes, 'indexes', $searchFilter);
        foreach ($elements as $i => $element) {
            $this->assertEquals($elementsArray[$i], $element->getValues());
        }
    }

    /**
     * @throws \Exception
     */
    public function testFacetFiltersSearch()
    {
        $index = new ElasticSearchIndexFilter(new IndexFilterParams([Product::class]));

        $element1 = new MatchFilter(new MatchFilterParams('brandId', '93'));

        $searchFilter = (new SearchFilter())->index([$index])
            ->elements([$element1]);

        $element2 = new MatchFilter(new MatchFilterParams('functionalNameId', '188'));
        $mustNotFilter = (new MustNotSearchFilter())->index([$index])->elements([$element2]);

        $queryFilter = (new QueryFilter())->index([$index])
            ->queries([$searchFilter, $mustNotFilter]);

        $connect = $this->client->elasticSearch();
        $records = $connect->filter($queryFilter)->all();

        $this->assertEquals(true, true);
    }

